import React, { Component, useRef } from 'react';
import { get } from '@tonaljs/note'
import Guitar, { getRenderFingerSpn, fromMidiSharps } from "react-guitar";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


export const parse = (string) => {
  return string
    .split(' ')
    .map((name) => get(name).midi ?? (get(name).chroma ?? 0) + 12 * 3)
    .reverse()
}
let tunings = parse('E2 A2 D3 G3 B3 E4')
let strings = [[-1, -1, 7, 6, 5, 0], [0, 0, 1, 4, 2, 3], [0, 0, 1, 4, 2, 3]]
class App extends Component {
  construtor() {
    this.state = {value: [[-1, -1, 2, 2, 1, 0], [0, 0, -1, 2, 2, 3], [-1, 0, 1,-1, 2, 3],[-1, 0, 1,-1, 2,,-1]]}
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleSubmit(event) {

  }


  render() {


    return (
      <>
        <div>
          <form onSubmit={this.handleSubmit}>
            <label>
              Pick your favorite flavor:
          <select value={this.state.value}>
                <option value={[[-1, -1, 2, 2, 1, 0], [0, 0, -1, 2, 2, 3], [-1, 0, 1,-1, 2, 3],[-1, 0, 1,-1, 2,,-1]]}>Tuning 1</option>
                <option value={[[-1, -1, 2, 2, 1, 0], [0, 0, -1, 2, 2, 3]]}>Tuning 2</option>
                <option value={[[-1, -1, 2, 2, 1, 0]]}>Tuning 3</option>
              <option value={[[-1, -1, 2, 2, 1, 0],[0,0,0,0,1,2]]}>Tuning 4</option>
          </select>
        </label>
        <input type="submit" value="Submit" />
      </form>
      </div >
      <div className="container">
        <div className="row">
          <div className="container">
            <image src="lol.png" width="300" height="150" />
          </div>
        </div>
        <div className="row">
          <div className="container">
            {strings.map((string) => (
              <>
                <div className="row">
                  <Guitar
                    strings={string}
                    renderFinger={getRenderFingerSpn(tunings)}
                  />
                </div>
              </>
            ))}
          </div>
        </div>
      </div>
      </>


      // document.getElementById("root")

    );
  }


}


export default App;
