import React, { Component, useRef } from 'react';
import { get } from '@tonaljs/note'
import Guitar, { getRenderFingerSpn, fromMidiSharps } from "react-guitar";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';


export const parse = (string) => {
  return string
    .split(' ')
    .map((name) => get(name).midi ?? (get(name).chroma ?? 0) + 12 * 3)
    .reverse()
}
let tunings = parse('E2 A2 D3 G3 B3 E4')
let strings = [[-1, -1, 7, 6, 5, 0], [0, 0, 1, 4, 2, 3], [0, 0, 1, 4, 2, 3]]
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {value: '',loading:false};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {    this.setState({value: event.target.value});  }
  handleSubmit(event) {
    if (this.state.value==1){
    strings = [[-1, -1, 2, 2, 1, 0], [0, 0, -1, 2, 2, 3], [-1, 0, 1,-1, 2, 3],[-1, 0, 1,-1, 2,,-1]]
    } else if (this.state.value==2){
      strings = [[-1, -1, 2, 2, 1, 0], [0, 0, -1, 2, 2, 3]]
    } else if (this.state.value==3){
      strings = [[-1, -1, 2, 2, 1, 0]]
    }else if (this.state.value==4){
      strings =  [[-1, -1, 2, 2, 1, 0],[0,0,0,0,1,2]]
    }else if (this.state.value==5){
      strings = [[2,3,4,-1,0,3]]
    }else if (this.state.value==6){
      strings = [[-4, 0, -2, -4, -2, 2], [2, -1, -4, -4, -2, -3]]

    }else if (this.state.value==7){
      strings =[[-4, 0, -2, -4, -2, 2], [2, -1, -4, -4, -2, -3]]

    }else if (this.state.value==8){
      strings = [[3, 1, 1, 4, -1, 3], [2, -1, -1, 0, -1, -3]]
    }else if (this.state.value==9){
      strings = [[3, -1, 3, -3, 0, -3], [-1, 1, -2, 1, -1, -2], [-4, -4, 2, 1, -2, 3]]

    }else if (this.state.value==10){
      strings = [[4, 1, -1, 1, -1, 2], [3, 0, -1, -3, -1, 4], [0, -4, -3, 2, -4, 4]]

    }else if (this.state.value==11){
      strings =[[-3, 0, 3, 2, -3, -2], [-2, -4, -1, -1, 0, -1]]
    }
    this.setState()

  }


  render() {

     let  guitar= (<>
     <div className="container">
        <div className="row">
          <div className="container">
            <image src="logo.svg" width="300" height="100" />
          </div>
        </div>
        <div className="row">
          <div className="container">
            {strings.map((string) => (
              <>
                <div className="row">
                  <Guitar
                    strings={string}
                    renderFinger={getRenderFingerSpn(tunings)}
                  />
                </div>
              </>
            ))}
          </div>
        </div>
      </div>
    </>)

    return (
      <>
        <div>
        <form >        <label>
          Config:
          <input type="number" value={this.state.value} onChange={this.handleChange} />        </label>
        <input  type="button" onClick={this.handleSubmit} value="Submit" />
      </form>
      </div >
      {guitar}
      </>


      // document.getElementById("root")

    );
  }


}


export default App;
